css:
.section-title {
  border-bottom: 1px solid #b40000;
  padding-bottom: 5px;
  margin-top: 14px;
  margin-bottom: 10px;
  font-weight: 700;
}

/* --- Main (pretty) tables --- */
.form-table {
  width: 100%;
  border-collapse: separate;
  border-spacing: 0;
  border: 1px solid #ddd;
  border-radius: 8px;
  overflow: hidden;
  margin-bottom: 14px;
  table-layout: fixed;
}

.form-table th,
.form-table td {
  padding: 8px;
  font-size: 11pt;
  vertical-align: top;
}

.form-table th {
  background: #f5f5f5;
  border-bottom: 1px solid #ddd;
  text-align: left;
  font-weight: 700;
}

.form-table td {
  border-bottom: 1px solid #ddd;
  border-right: 1px solid #ddd;
}

.form-table td:last-child,
.form-table th:last-child {
  border-right: none;
}

.form-table tbody tr:last-child td {
  border-bottom: none;
}

/* --- Signature grids (hard outlines) --- */
.sig-grid {
  width: 100%;
  border-collapse: collapse;
  table-layout: fixed;
  border: 1px solid #000 !important;
  margin-top: 10px;
  margin-bottom: 12px;
}

.sig-grid th,
.sig-grid td {
  border: 1px solid #000 !important;
  padding: 8px;
  font-size: 11pt;
  vertical-align: top;
}

.sig-grid th {
  font-weight: 700;
  text-align: center;
  background: #f5f5f5;
}

.sig-rowlabel {
  width: 14%;
  font-weight: 700;
  background: #fff;
}

.small-muted {
  font-size: 10pt;
  color: #666;
  margin-top: 8px;
}

@page {
  margin: 20mm 15mm 25mm 15mm;
}
