css:
.print-heading {
  text-align: center;
  margin-bottom: 30px;
}

.print-heading h2 {
  margin: 0;
  font-size: 18pt;
}

table th { display: none; }

.war-table {
  width: 100%;
  border-collapse: separate;  /* Needed for radius */
  border-spacing: 0;
  border: 1px solid #ddd;
  border-radius: 8px;
  overflow: hidden;
  margin-bottom: 20px;
}

.war-table th,
.war-table td {
  padding: 8px;
}

/* Top row background */
.war-table th {
  background-color: #f5f5f5;
  border-bottom: 1px solid #ddd;
}

/* All table cells: bottom + right borders for grid lines */
.war-table td {
  border-bottom: 1px solid #ddd;
  border-right: 1px solid #ddd;
}

/* Remove right border on last column */
.war-table td:last-child,
.war-table th:last-child {
  border-right: none;
}

/* Remove bottom border on last row */
.war-table tbody tr:last-child td {
  border-bottom: none;
}

/* Zebra stripes */
.war-table tbody tr:nth-child(odd) {
  background-color: #fafafa;
}

.signatures table {
  width: 100%;
  border-collapse: collapse;
  margin-top: 30px;
}

.signatures td {
  border: none;           /* No borders here */
  padding: 20px 0;        /* Spacing for signatures */
  vertical-align: top;
}

.signatures p {
  margin: 0;
  font-size: 12pt;
}

@page {
  @bottom-right {
    content: "Page " counter(page) " of " counter(pages);
    font-size: 10pt;
    color: #999;
  }
}
